import axios from 'axios';
import React, { useContext, useEffect, useState } from 'react';
import { Button, Carousel, Container, Form, InputGroup, Modal } from 'react-bootstrap';
import { UserContext } from '../../../contexts/UserContext';
import ImageModify from './ImageModify';
import ImageReply from './ImageReply';

const ImageDetail = ({ show, onClose, selectedPhotoInfo, somoimId, isAdmin }) => {
    console.log('Selected Photo Info:', selectedPhotoInfo);
    console.log('selectedPhotoInfo.user.id', selectedPhotoInfo.user.id)
    
    const [activeIndex, setActiveIndex] = useState(0); // Ïù¥ÎØ∏ÏßÄ Ï∫êÎü¨ÏÖÄ Íµ¨ÌòÑ
    const [maxHeight, setMaxHeight] = useState(null); // Ï∫êÎü¨ÏÖÄ ÌÅ¨Í∏∞ Í≥†Ï†ï Íµ¨ÌòÑ
    const [hitCount, setHitCount] = useState(selectedPhotoInfo ? selectedPhotoInfo.hit : 0); // Ï°∞ÌöåÏàò ÏÉÅÌÉú Ï∂îÍ∞Ä
    const [isLiked, setIsLiked] = useState(false); // Ï¢ãÏïÑÏöî Íµ¨ÌòÑ
    const [likedCount, setLikeCount] = useState() // Ï¢ãÏïÑÏöî Í∞úÏàò
    const [comments, setComments] = useState([{}]); // ÎåìÍ∏Ä Î™©Î°ù ÏÉÅÌÉú Î≥ÄÏàò

    const [isModifyMode, setIsModifyMode] = useState(false); // ÏàòÏ†ï Î™®Îìú Ï∂îÍ∞Ä
    const [modifiedPhotoInfo, setModifiedPhotoInfo] = useState(null); // ÏàòÏ†ïÌï† Ïù¥ÎØ∏ÏßÄ Ï†ïÎ≥¥ Ï∂îÍ∞Ä

    const { user } = useContext(UserContext); // Ïú†Ï†Ä Ïª®ÌÖçÏä§Ìä∏
    console.log('Ïù¥ÎØ∏ÏßÄÎîîÌÖåÏùº user : ', user.id)
     // ÏÑ†ÌÉùÎêú Ïù¥ÎØ∏ÏßÄ Í≤åÏãúÍ∏Ä Ï†ïÎ≥¥
     const { createdAt, modifiedAt, id, photoTitle, photoContent, 
        photoLink, photoLike, hit, somoimPhoto, somoim } = selectedPhotoInfo;

    // console.log('ÏÇ¨ÏßÑÏ≤© ÎîîÌÖåÏùº somoimId : ', somoim.id)
    
    useEffect(() => {
        console.log('ImageDetail - Selected Photo Info:', selectedPhotoInfo);
        console.log('ImageDetail - Ìï¥Îãπ Í≤åÏãúÍ∏ÄÏùò ÎåìÍ∏Ä Î™©Î°ù Ï†ïÎ≥¥ :', comments);
      }, [selectedPhotoInfo, comments, comments.user]);

    //Ï°∞ÌöåÏàò Í≥ÑÏÇ∞
    useEffect(() => {
        // Ïª¥Ìè¨ÎÑåÌä∏Í∞Ä Ï≤òÏùå Î†åÎçîÎßÅÎê† ÎïåÎäî updateHitCountÎ•º Ìò∏Ï∂úÌïòÏßÄ ÏïäÏäµÎãàÎã§.
        if (selectedPhotoInfo && show) {
            updateHitCount();
        }
    }, [show, selectedPhotoInfo]);

    // Ìï¥Îãπ Í∏ÄÏùò Í∞ÄÏû• ÌÅ∞ Ïù¥ÎØ∏ÏßÄÎ•º Í∏∞Ï§ÄÏúºÎ°ú Î™®Îã¨Ï∞Ω ÎÜíÏù¥ Í≥ÑÏÇ∞
    useEffect(() => {
      // Ïù¥ÎØ∏ÏßÄÏùò ÏµúÎåÄ ÎÜíÏù¥ Í≥ÑÏÇ∞
      const calculateMaxHeight = () => {
        const imageElements = document.querySelectorAll('.carousel-inner img');
        let maxImageHeight = 0;
  
        imageElements.forEach((img) => {
          maxImageHeight = Math.max(maxImageHeight, img.clientHeight);
        });
  
        setMaxHeight(maxImageHeight);
      };
  
      calculateMaxHeight();
  
      // Ï∞Ω ÌÅ¨Í∏∞Í∞Ä Î≥ÄÍ≤ΩÎê† ÎïåÎßàÎã§ ÏµúÎåÄ ÎÜíÏù¥ Îã§Ïãú Í≥ÑÏÇ∞
      const handleResize = () => {
        calculateMaxHeight();
      };
  
      window.addEventListener('resize', handleResize);
  
      return () => {
        window.removeEventListener('resize', handleResize);
      };
    }, [show]);

    
    useEffect(() => {
        // Ï¢ãÏïÑÏöî Ïó¨Î∂Ä ÏöîÏ≤≠
        const LikeStatus = async () => {
          try {
            const response = 
                //await axios.get(`/somoim/somoimPhotoLikeStatus?userId=${user.id}&photoId=${id}`);
                await axios.post(`/somoim/somoimPhotoLikeStatus`, {
                    userId: user.id,
                    photoId: id
                  });
            setIsLiked(response.data.isLiked);
            console.log('ÏÇ¨ÏßÑÏ≤© Ï¢ãÏïÑÏöî Îì±Î°ù Ïó¨Î∂Ä ÌôïÏù∏ : ', response.data.isLiked)
          } catch (error) {
            console.error('Error fetching like status:', error);
          }
        };

        LikeStatus();
      }, [id, user]);

    useEffect(() => {
        // Ï¢ãÏïÑÏöî Î∞õÏùÄ Í∞úÏàò ÏöîÏ≤≠
        const ListCount = async () => {
            try {
                const res = await axios.get(`/somoim/somoimPhotoLikeCount?photoId=${id}`)
                setLikeCount(res.data);
                console.log('Ìï¥Îãπ Í≤åÏãúÍ∏ÄÏù¥ Î∞õÏùÄ Ï¢ãÏôÄÏöî Í∞úÏàò : ', res.data)
            } catch(error) {
                console.error('Ï¢ãÏïÑÏöî Í∞úÏàò Ï°∞Ìöå ÏóêÎü¨', error)
            }
        }

        ListCount();
    }, [id, isLiked]);
    
    // Ï¢ãÏïÑÏöî Îì±Î°ù Î∞è Ï∑®ÏÜå
    const handleLikeToggle = async () => {
        try {
            const config = {
                headers: {
                    'Content-Type': 'application/json',
                },
                data: {
                    userId: user.id,
                    photoId: id,
                },
            };
    
            if (isLiked) {
                // Ï¢ãÏïÑÏöî Ï∑®ÏÜå
                await axios.delete('/somoim/somoimPhotoLikes', config)
                    .then(res => 
                        console.log('Ï¢ãÏïÑÏöî Ï∑®ÏÜå ÌôïÏù∏ : ', res.data),
                        )
                    .catch(e => console.log(e));
            } else {
                // Ï¢ãÏïÑÏöî Îì±Î°ù
                await axios.post('/somoim/somoimPhotoLikes', { 
                    userId: user.id,
                    photoId: selectedPhotoInfo.id
                }).then(res => console.log('Ï¢ãÏïÑÏöî Îì±Î°ù ÌôïÏù∏ : ', res.data))
                  .catch(e => console.log(e));
            }
    
            setIsLiked(!isLiked);
        } catch (error) {
            console.error('Error toggling like:', error);
        }
    };

    // ÏÜåÎ™®ÏûÑ Î≤àÌò∏ÏôÄ Ïú†Ï†Ä Í∂åÌïú Ï∂úÎ†•
    useEffect(() => {
        console.log('ÏÇ¨ÏßÑÏ≤© ÎîîÌÖåÏùº somoimId : ', somoimId)
        console.log('ÏÇ¨ÏßÑÏ≤© ÎîîÌÖåÏùº isAdmin : ', isAdmin)
        console.log('ÏÇ¨ÏßÑÏ≤© ÎîîÌÖåÏùº photoId : ', id)
    },[isAdmin, somoimId, id])

    if (!selectedPhotoInfo) {
        // ÏÑ†ÌÉùÎêú Ïù¥ÎØ∏ÏßÄ Ï†ïÎ≥¥Í∞Ä ÏóÜÏùÑ Í≤ΩÏö∞ Î™®Îã¨ÏùÑ Î†åÎçîÎßÅÌïòÏßÄ ÏïäÏùå
        return null;
    }
    
    // Ï°∞ÌöåÏàò Í≥ÑÏÇ∞ axios
    const updateHitCount = async () => {
        try { // ?id=${id}
            const response = await axios
                    .post(`/somoim/somoimPhotoCount?id=${id}`);
            setHitCount(response.data.hit);
        } catch (error) {
            console.error('Failed to update hit count:', error);
        }
    };

    const photoArray = somoimPhoto.replace(/"/g, '').split(',');

    const handleSelect = (selectedIndex, e) => {
        setActiveIndex(selectedIndex);
    };

    // ÏàòÏ†ï Î≤ÑÌäº ÌÅ¥Î¶≠ Ïãú Ìò∏Ï∂ú
    const handleModifyButtonClick = (selectedPhotoInfo) => {
        setModifiedPhotoInfo(selectedPhotoInfo);
        setIsModifyMode(true);
      };
    
    const handleClose = () => {
        setModifiedPhotoInfo(null);
        setIsModifyMode(false);
    };

    // Í∏ÄÏÇ≠Ï†ú
    const onDeletePhoto = async () => {
        const confirm = window.confirm('Ï†ïÎßêÎ°ú ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?')
        if(confirm) {
            if(isAdmin !== 0 && isAdmin !== 1) {
                alert('ÏÇ≠Ï†ú Í∂åÌïúÏù¥ ÏóÜÏäµÎãàÎã§.')
            } else {
                // await axios.delete('/somoim/somoimPhotoDelete', {
                //         data: {
                //             id: id,
                //             userId: user.id,
                //             somoimId: somoim.id
                //         }
                //     })
                await axios.delete(`/somoim/somoimPhotoDelete?id=${id}&userId=${user.id}&somoimId=${somoim.id}`)
                    .then((res) => {
                        console.log('ÏÇ≠Ï†ú ÏùëÎãµ:', res);
                        alert('Ìï¥Îãπ Í≤åÏãúÍ∏ÄÏù¥ ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.')
                        onClose();
                        window.location.reload();
                    })
                    .catch((error) => {
                        console.error('ÏÇ≠Ï†úÏãú ÏóêÎü¨', error);
                    });
            }//isAdmin ifÎ¨∏
        }//confirm ifÎ¨∏
    }

    // ÎåìÍ∏Ä Îì±Î°ù ÏΩúÎ∞± Ìï®Ïàò
    // const onSubmitComment = (comment) => {
    //     // ÏÑúÎ≤ÑÏóê ÎåìÍ∏Ä Îì±Î°ù ÏöîÏ≤≠ Îì±Ïùò Î°úÏßÅ ÏàòÌñâ
    //     // ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Îì±Î°ùÎêòÎ©¥ ÏÑúÎ≤ÑÏóêÏÑú Î∞òÌôòÌïú ÎåìÍ∏Ä Ï†ïÎ≥¥Î•º comments ÏÉÅÌÉúÏóê Ï∂îÍ∞Ä
    //     const newComment = {
    //     id: comments.length + 1, // Ï†ÅÏ†àÌïú Î∞©ÏãùÏúºÎ°ú ÎåìÍ∏Ä id ÏÉùÏÑ±
    //     content: comment,
    //     // Ï∂îÍ∞ÄÏ†ÅÏù∏ ÎåìÍ∏Ä Ï†ïÎ≥¥ ÌïÑÎìú Ï∂îÍ∞Ä Í∞ÄÎä•
    //     };

    //     setComments([...comments, newComment]);
    // };


    // ÎåìÍ∏Ä Î™©Î°ù Ï†ÑÎã¨Î∞õÍ∏∞
    const updateComments = (newComments) => {
        const filteredComments = newComments
            .filter(comment => comment.somoimPhoto.id === selectedPhotoInfo.id)
            .map((comment, index) => ({ ...comment, sequence: index + 1 }));
    
        // sequenceÎßå Îí§ÏßëÍ∏∞
        const reversedComments = filteredComments.map((comment, index) => ({
            ...comment,
            sequence: filteredComments.length - index, // Ïó≠ÏàúÏúºÎ°ú Î≥ÄÍ≤ΩÎêú ÏàúÏÑúÎ•º Ï†ÅÏö©
        }));
    
        setComments(reversedComments);
    };


    // ÎåìÍ∏Ä ÏÇ≠Ï†ú
    const onReplyDeleteBtn = (selectedReply) => {
        console.log('selectedReply', selectedReply)
        if(window.confirm('Ï†ïÎßê ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?')) {
            axios.delete(`/somoim/photoReplyDelete?commentId=${selectedReply.id}&userId=${user.id}`)
              .then((response) => {
                console.log('ÎåìÍ∏Ä ÏÇ≠Ï†ú ÏùëÎãµ:', response);
                alert('Ìï¥Îãπ ÎåìÍ∏ÄÏù¥ ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.')

                // ÏÉàÎ°úÏö¥ Î∞∞Ïó¥ÏùÑ ÏÉùÏÑ±ÌïòÏó¨ ÏÇ≠Ï†úÎêú ÎåìÍ∏ÄÏùÑ Ï†úÏô∏Ìïú ÎåìÍ∏Ä Î™©Î°ùÏùÑ ÎßåÎì≠ÎãàÎã§.
                const updatedComments = comments.filter(comment => comment.id !== selectedReply.id);
                
                // ÏÉàÎ°úÏö¥ Î∞∞Ïó¥ÏùÑ ÏÉÅÌÉúÎ°ú ÏÑ§Ï†ïÌï©ÎãàÎã§.
                setComments(updatedComments);
              })
              .catch((error) => {
                console.error('ÎåìÍ∏Ä ÏÇ≠Ï†ú ÏóêÎü¨:', error);
              });
        }
    };

    // Í≤åÏãúÍ∏Ä ÎÇ†Ïßú ÌòïÏãù Î≥ÄÌôò
    function formatDate(dateString) {
        const options = {
          year: 'numeric',
          month: '2-digit',
          day: '2-digit',
          hour: '2-digit',
          minute: '2-digit',
          second: '2-digit',
        };
      
        const formattedDate = new Date(dateString).toLocaleDateString('ko-KR', options)
          .replace('.', 'ÎÖÑ')
          .replace('.', 'Ïõî')
          .replace('.', 'Ïùº')
          .replace(' ', ' ')
          .replace(/(\d+:\d+:\d+)/, '$1');
          
        return formattedDate;
    }

    // ÎåìÍ∏Ä ÎÇ†Ïßú ÌòïÏãù Î≥ÄÌôò
    function ReplyDate(dateString) {
        const options = {
          year: 'numeric',
          month: '2-digit',
          day: '2-digit',
          hour: '2-digit',
          minute: '2-digit',
          second: '2-digit',
        };
      
        const formattedDate = new Date(dateString).toLocaleDateString('ko-KR', options)
          .replace('.', 'ÎÖÑ')
          .replace('.', 'Ïõî')
          .replace('.', 'Ïùº')
          .replace(' ', ' ')
          .replace(/(\d+:\d+:\d+)/, '$1');
          
        return formattedDate;
    }

    return (
        <Modal className="carousel-container" show={show} onHide={onClose} size="lg">
            {isModifyMode ? (
                <ImageModify onClose={handleClose} modifiedPhotoInfo={modifiedPhotoInfo} />
            ) : (
            <div style={{border: '1px solid red'}}>
                <Modal.Header closeButton style={{ padding: '0.8rem', backgroundColor: 'pink', color: 'white' }}>
                    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', width: '95%' }}>
                        <Modal.Title>
                        Ï†úÎ™© : <strong>{photoTitle}</strong>
                        </Modal.Title>        
                        <span style={{ marginLeft: 'auto' }}>
                            <button 
                                onClick={ handleLikeToggle } 
                                style={{ marginLeft: 'auto', marginRight:'1rem', backgroundColor: 'pink' }}
                            >
                                { isLiked ? '‚ù§Ô∏è' : 'ü§ç' }{ likedCount }
                            </button>
                            Ï°∞ÌöåÏàò : {hitCount}</span>
                    </div>
                </Modal.Header>
                <Modal.Body
                className="d-flex justify-content-center m-5"
                style={{ height: maxHeight ? maxHeight : '50%', minHeight: '200px', overflow: 'hidden', display: 'flex', flexDirection: 'row' }}
                >
                    <style>
                        {`
                            .carousel-indicators {
                                transform: translateY(30px);
                            }
                        `}
                    </style>
                    <div className="col-8" style={{ display: 'flex', flexWrap: 'wrap', maxHeight: '100%' }}>
                        <Carousel data-bs-theme="dark"activeIndex={activeIndex} onSelect={handleSelect} interval={null} style={{ width: '100%', textAlign: 'center', maxHeight: '100%' }}>
                            {photoArray.map((photo, index) => (
                                <Carousel.Item key={index}>
                                <img
                                    key={index}
                                    src={`https://kr.object.ncloudstorage.com/bitcamp-edu-bucket-112/${photo}`}
                                    alt={`somoimPhoto-${index}`}
                                    style={{ width: '50%', marginBottom: '10px', maxHeight: '100%' }}
                                />
                            </Carousel.Item>
                        ))}
                        </Carousel>
                    </div>
                    <div className="col-4" style={{ maxHeight: '100%', overflowY: 'auto' }}>
                        <strong style={{ lineHeight:'2.5' }}>ÎÇ¥Ïö© :</strong><br />
                        <div style={{ whiteSpace: 'pre-line' }}>{photoContent}</div>
                    </div>
                </Modal.Body>
                <div style={{ display: 'flex', marginRight: '1rem', marginLeft: '1rem', marginBottom: '0.5rem' }}>
                    <div style={{ marginRight: 'auto' }}>‚Äª ÏûëÏÑ±Ïûê: {selectedPhotoInfo.user.name}</div>
                    {
                    modifiedAt ? (
                        <div style={{ marginLeft: 'auto' }}>
                            (ÏàòÏ†ïÏùº : {formatDate(modifiedAt)})</div>
                    ) : (
                        <div style={{ marginLeft: 'auto' }}>
                            (ÏûëÏÑ±Ïùº : {formatDate(createdAt)})</div>
                    )
                }
                </div>
                <ImageReply selectedPhotoInfo={selectedPhotoInfo} onUpdateComments={updateComments} />
                { comments.length === 0 ? (
                    <div style={{ textAlign: 'center', padding: '20px', marginBottom: '10px', color: 'gray' }}>
                        ÏïÑÏßÅ ÎåìÍ∏ÄÏù¥ ÏóÜÏäµÎãàÎã§.
                    </div>
                ) : (
                    <>
                        {
                            comments.map((item, index) => { 
                                return (
                                <Container className='py-3 px-4' 
                                        key={index}
                                        style={{
                                            borderBottom: '1px solid pink',
                                            cursor: 'pointer', 
                                            transition: 'background-color 0.3s', 
                                        }}
                                        onMouseOver={(e) => { e.currentTarget.style.backgroundColor = '#F0E6ED'; }}
                                        onMouseOut={(e) => { e.currentTarget.style.backgroundColor = ''; }}
                                    >
                                    <div style={{ display: 'flex', justifyContent: 'left', alignItems: 'center' }}>
                                        <div className='col-1' style={{ textAlign: 'center' }}>{item.sequence}</div>
                                        <div className='col-7 px-4' style={{ overflowWrap: 'break-word' }}>{item.comment}</div>
                                        <div className='col-4' style={{ }}>
                                            {item.modifiedAt ? 
                                                <div className='col' style={{ marginBottom: '0.5rem' }}>{ReplyDate(item.modifiedAt)}</div>  : 
                                                <div className='col' style={{ marginBottom: '0.5rem' }}>{ReplyDate(item.createdAt)}</div>
                                            }
                                            <div className='col' style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center'}}>
                                                <div style={{ alignSelf: 'flex-start' }}>ÏûëÏÑ±Ïûê: {item.user ? item.user.name : 'Î°úÎî©Ï§ë...'}</div>
                                                {
                                                    ( isAdmin === 1 || (user.id && comments[index]?.user?.id === user.id)) && (
                                                        <Button variant='danger' size='sm' onClick={() => onReplyDeleteBtn(comments[index])}>
                                                            ÏÇ≠Ï†ú
                                                        </Button>
                                                    )
                                                }
                                            </div>
                                        </div>
                                    </div>
                                </Container>
                            )})
                        }
                    </>
                )}
                <Modal.Footer style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', backgroundColor: 'pink', color: 'white' }}>
                    <div>
                        ÎßÅÌÅ¨ : <a href={photoLink} target="_blank" rel="noopener noreferrer">{photoLink}</a>
                    </div>
                    <div style={{ marginRight: '0.5rem', gap: '2' }}>
                    {isAdmin === 1 || ((selectedPhotoInfo.user && selectedPhotoInfo.user.id) === user?.id) && (
                        <>
                            <Button variant="danger" size="sm" onClick={() => handleModifyButtonClick(selectedPhotoInfo)}>ÏàòÏ†ï</Button>
                            <Button variant="danger" size="sm" onClick={onDeletePhoto} style={{ marginLeft: '0.7rem' }}>ÏÇ≠Ï†ú</Button>
                        </>
                    )}
                    </div>
                </Modal.Footer>
            </div>
            )}
        </Modal>
      );
    };

export default ImageDetail;
