import React, { useContext, useEffect, useRef, useState } from "react";
import Header from "../main/Header";
import Footer from "../main/Footer";
import {
  Button,
  Col,
  Container,
  Form,
  InputGroup,
  Modal,
  Row,
} from "react-bootstrap";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";

import styles from "../../css/somoim/main/somoimNew.module.css";
import { UserContext } from "../../contexts/UserContext";
import Reactquill from "./Reactquill";
import SomoimPostcode from "./SomoimPostcode";

const SomoimNew = () => {
  const [formData, setFormData] = useState({
    somoimName: "",
    introduceSub: "",
    introduceDetail: "",
    memberCount: "",
    location: "",
    target: "",
    address: "",
    address2: "",
    zipcode: "",
    accountName: "",
    accountEmail: "",
    accountPhone: "",
  });

  // 스크롤 강제로 위로 올리기
  const location = useLocation();
  useEffect(() => {
      window.scrollTo(0, 0);
  }, [location.pathname]);

  //const [introducePhotoName, setIntroducePhotoName] = useState('');
  const [file, setFile] = useState(""); // 파일 업로드
  const [showImgSrc, setShowImgSrc] = useState(""); // 이미지 미리보기

  const [isSmallScreen, setIsSmallScreen] = useState(window.innerWidth > 992); // 이미지 업로드 그리드 반응형설정

  const [modalState, setModalState] = useState(false); // 주소 모달창

  useEffect(() => {
    const handleResize = () => {
      setIsSmallScreen(window.innerWidth > 992);
    };
    window.addEventListener("resize", handleResize);
    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  const [isEditorDisabled, setIsEditorDisabled] = useState(false); // React-Quill 상태변수

  //const [createdId, setCreatedId] = useState('John Doe')
  const [validated, setValidated] = useState(false); // 유효성 검사
  //const [showAlert, setShowAlert] = useState(false);

  const navigate = useNavigate();

  const {
    somoimName,
    introduceSub,
    introduceDetail,
    memberCount,
    target,
    address,
    address2,
    zipcode,
    accountEmail,
    accountPhone,
  } = formData;

  const { user } = useContext(UserContext); // 유저 컨텍스트
  const { name, id } = user;
  
  useEffect(() => {
    console.log('소모임 개설페이지 name : ', name);
  },[user, name])


  const imgRef = useRef(); // 초점 맞추기
  const onCamera = () => {
    imgRef.current.click();
  };
  const searchAddress = () => {
    // 주소 컴포넌트 호출
    console.log("주소버튼");
    setModalState(true);
  };

  const handleAddressChange = (data) => {
    // 주소 입력값 데이터 담기
    setFormData({
      ...formData,
      zipcode: data.zonecode,
      address:
        data.userSelectedType === "R" ? data.roadAddress : data.jibunAddress,
      address2: "",
    });

    setModalState(false);
  };

  const readURL = (input) => {
    // 파일 업로드
    if (input.files && input.files[0]) {
      var reader = new FileReader();
      reader.readAsDataURL(input.files[0]);
      reader.onload = () => {
        console.log(input.files[0]);
        setShowImgSrc(reader.result);
        setFile(input.files[0]);
      };
    } else {
      // 파일이 선택되지 않은 경우 (취소된 경우) 처리
      if (showImgSrc) {
        return showImgSrc;
      } else {
        setShowImgSrc(""); // 기존 이미지 소스 지우기
        //setFile(''); // 파일 상태 지우기
      }
    }
  };

  useEffect(() => {
    console.log("유효성 통과 상태 : " + validated);
  }, [validated]);

  useEffect(() => {
    // setFile과 setFormData가 완료된 후에 실행
    // setFormData({
    //     ...formData,
    //     introducePhotoName: file,
    //});
    console.log(file.name);
    // const RealUpdate  = new FormData();
    // RealUpdate.append("formData", formData);
  }, [file]);
  //console.log(formData.introducePhotoName.name);

  if (file instanceof File) {
    console.log("이 객체는 File 클래스의 인스턴스입니다.");
  } else {
    console.log("이 객체는 File 클래스의 인스턴스가 아닙니다.");
  } // 여기까지 파일 업로드

  const handleChange = (e) => {
    const { name, value } = e.target;
    //console.log(`Name: ${name}, Value: ${value}`);
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleContentChange = (e) => {
    // setFormData({
    //     ...formData,
    //     introduceDetail: e,
    // });

    setFormData((prevFormData) => ({
      ...prevFormData,
      introduceDetail: e,
    }));
    console.log("상세정보 :   " + formData.introduceDetail);
  };

  useEffect(() => {
    console.log("상세정보: " + formData.introduceDetail.e);
  }, [formData.introduceDetail.e]);

  const handleLocationChange = (e) => {
    const text = e.target.options[e.target.selectedIndex].text;

    setFormData({
      ...formData,
      location: text,
    });

    console.log("Selected Location:", text);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const form = e.currentTarget;

    console.log("Form Data:", formData);

    if (form.checkValidity() === false) {
      e.preventDefault();
      e.stopPropagation();
    } else {
      setValidated(form.checkValidity() === true);

      try {
        const confirmed = window.confirm("정말 소모임을 개설하시겠습니까?");

        if (confirmed) {
          const updatedFormData = {
            somoim: {
              ...formData,
              accountName: name,
            },
            user: {
              id,
            },
            // ...formData,
            //user: { id }
            //user: { id : id }
          };
          console.log(
            "updatedFormData의 유저시퀀스번호 : " + updatedFormData.user.id
          );

          const RealUpdate = new FormData();
          RealUpdate.append("introducePhoto", file); // 이미지 파일 추가

          const jsonBlob = new Blob([JSON.stringify(updatedFormData)], {
            type: "application/json",
          });
          RealUpdate.append("updatedFormData", jsonBlob);

          //RealUpdate.append('updatedFormData', updatedFormData);
          // RealUpdate.append('somoimName', updatedFormData.somoimName);
          // RealUpdate.append('introduceSub', updatedFormData.introduceSub);
          // RealUpdate.append('introduceDetail', updatedFormData.introduceDetail);
          // RealUpdate.append('memberCount', updatedFormData.memberCount);
          // RealUpdate.append('cost', updatedFormData.cost);
          // RealUpdate.append('location', updatedFormData.location);
          // RealUpdate.append('target', updatedFormData.target);
          // RealUpdate.append('address', updatedFormData.address);
          // RealUpdate.append('address2', updatedFormData.address2);
          // RealUpdate.append('accountEmail', updatedFormData.accountEmail);
          // RealUpdate.append('accountEmail', updatedFormData.accountPhone);
          // const RealLastUpdate = {
          //     somoim: RealUpdate,
          //     user: {
          //       id
          //     },
          //   };

          console.log("updatedFormData");
          console.log(RealUpdate.get("updatedFormData"));
          console.log("introducePhoto");
          console.log(RealUpdate.get("introducePhoto"));

          const response = await axios
            .post("/somoim/somoimNewWrite", RealUpdate, {
              headers: {
                //'Content-Type': 'application/json',
                "Content-Type": "multipart/form-data",
              },
            })
            .then(
              alert("소모임 개설이 완료되었습니다."),
              navigate("/somoim")
              // res =>  {
              //     // const response = await axios.post('/somoim/somoimNewWrite',
              //     // JSON.stringify(updatedFormData),
              //     // {
              //     //     headers: {
              //     //         'Content-Type': 'application/json',
              //     //     },
              //     // }).then(res =>  {
              //         alert('소모임 개설이 완료되었습니다');
              //         //setShowAlert(true);
              //         navigate('/somoim');
              //     }
            )
            .catch((error) => console.log(error));
          // const response = await axios.post('/somoim/somoimNewWrite',
          //     JSON.stringify(updatedFormData),
          //     {
          //         headers: {
          //             'Content-Type': 'application/json',
          //         },
          //     }).then(res =>  {
          //         alert('소모임 개설이 완료되었습니다');
          //         //setShowAlert(true);
          //         navigate('/somoim');
          //     })
          //     .catch(error => console.log(error));
          console.log("서버 응답 오류:", response);
        } else {
          console.log("소모임 개설이 취소되었습니다.");
        } //if
      } catch (e) {
        console.error("서버 요청 에러:", e);
      } //try-catch
    }
  };

  const badgeContainerStyle = {
    backgroundColor: "#F56084",
    display: "inline-block",
    borderRadius: "20px",
    textAlign: "center",
    marginBottom: "0.5rem",
    width: "70%",
  };

  const badgeTextStyle = {
    color: "#fff",
    display: "block",
  };

  return (
    <div>
      <Header></Header>
      <div style={{ width: "100%", textAlign: "center", height: "1rem" }}></div>
      <Container
        className="px-10 py-3"
        style={{
          border: "1px solid blue",
          display: "flex",
          flexDirection: "column",
        }}
      >
        <div className="row d-flex justify-content-center">
          {/* <div className='col-13 col-md-4' style={{ textAlign: 'left', alignSelf: 'flex-end' }}>소모임 개설입니다.</div> */}
          <div
            className={`${styles.moimMainTitle} col-13 col-md-4 d-none d-md-block mb-2`}
          >
            소모임 개설
          </div>
        </div>
        <div className="row" style={{ marginTop: "1.5rem" }}>
          <Form
            noValidate
            validated={validated}
            className="form-signup"
            onSubmit={handleSubmit}
          >
            {/* <Form.Group as={Row} className={`${styles.default} mb-4`} controlId="formPlaintextDetail"> */}
            <div className={isSmallScreen ? "d-flex" : ""}>
              <div className="row col-12 col-lg-8">
                <Form.Group as={Row} className={`${styles.default}  mb-4`}>
                  <Col sm>
                    {/* <Form.Label className={`${styles.defaultLayout}`} htmlFor="name">소모임 이름</Form.Label> */}
                    <Form.Label htmlFor="name">소모임 이름</Form.Label>
                    <span className={`${styles.badge}`}>필수</span>
                    <Form.Control
                      id="somoimName"
                      name="somoimName"
                      type="text"
                      value={somoimName}
                      onChange={handleChange}
                      placeholder="소모임 이름을 입력해 주세요 (10자 이내)"
                      required
                    />
                    <Form.Control.Feedback type="invalid">
                      소모임 이름을 입력해주세요!
                    </Form.Control.Feedback>
                  </Col>
                </Form.Group>

                <Form.Group as={Row} className={`${styles.default}  mb-3`}>
                  <Col sm>
                    {/* <Form.Label className={`${styles.defaultLayout}`} htmlFor="introduceSub">소모임 한 줄 소개</Form.Label> */}
                    <Form.Label htmlFor="introduceSub">
                      소모임 한 줄 소개
                    </Form.Label>
                    <span className={`${styles.badge}`}>필수</span>
                    <Form.Control
                      id="introduceSub"
                      name="introduceSub"
                      type="text"
                      value={introduceSub}
                      onChange={handleChange}
                      placeholder="소모임을 간단히 소개해 주세요 (100자 이내)"
                      required
                    />
                    <Form.Control.Feedback type="invalid">
                      소모임에 대한 한 줄정보를 입력해주세요!
                    </Form.Control.Feedback>
                  </Col>
                </Form.Group>
                <Form.Group as={Row} className={`${styles.default} mb-4`}>
                  <Col sm>
                    <Form.Label
                      className={`${styles.defaultInfo} ${styles.dataVbf668e03}`}
                      htmlFor="introduceSub"
                    >
                      모든 입력창은 필수 정보입니다. 빠짐없이 입력해주세요.
                    </Form.Label>
                  </Col>
                </Form.Group>

                {/*  ----------------원래 textarea 들어가던 구간------------------ */}
              </div>
              <div className={"row col-12 col-lg-5 justify-content-center"}>
                <Form.Group controlId="formFileSm" className="mb-3">
                  <Form.Label
                    className="d-flex justify-content-center"
                    id="showImgList"
                  >
                    {showImgSrc ? (
                      <img
                        src={showImgSrc}
                        alt={file}
                        style={{ width: "252px", height: "203px" }}
                      />
                    ) : (
                      <img
                        src="/image/somoim/null_image.png"
                        alt="빈 이미지"
                        style={{ width: "252px", height: "252px" }}
                      />
                    )}
                  </Form.Label>
                  <div
                    style={{
                      marginTop: "13px",
                      display: "flex",
                      justifyContent: "center",
                      marginBottom: "0px",
                      alignItems: "center",
                    }}
                  >
                    <div
                      style={{ ...badgeContainerStyle, cursor: "pointer" }}
                      onClick={onCamera}
                    >
                      <span style={badgeTextStyle}>
                        소모임 대표 이미지 업로드
                      </span>
                      <input
                        type="file"
                        name="img"
                        ref={imgRef}
                        onChange={(e) => readURL(e.target)}
                        id="img"
                        style={{ display: "none", width: "70%", height: "2px" }}
                      />
                    </div>
                  </div>
                  {/* <Form.Control 
                                    onChange={ e => readURL(e.target) }
                                    type="file" size="sm" 
                                    style={{ width: '70%' }} /> */}
                  <Form.Control.Feedback type="invalid">
                    소모임 대표 이미지를 넣어주세요!
                  </Form.Control.Feedback>
                </Form.Group>
              </div>
            </div>
            <Form.Group as={Row} className={`${styles.default} mb-4`}>
              <Col sm>
                <Form.Label
                  className={`${styles.defaultLayout}`}
                  htmlFor="introduceDetail"
                >
                  상세 정보
                </Form.Label>
                <Reactquill
                  id="introduceDetail"
                  name="introduceDetail"
                  value={introduceDetail}
                  //onChange={handleChange}
                  onContentChange={handleContentChange}
                  isDisable={isEditorDisabled}
                  required
                />
                {/* <Form.Control 
                                        id='introduceDetail' 
                                        name='introduceDetail' 
                                        value={introduceDetail}
                                        onChange={handleChange}
                                        as="textarea" rows={5} required />
                                 */}
                <Form.Control.Feedback type="invalid">
                  소모임에 대한 상세정보를 입력해주세요!
                </Form.Control.Feedback>
              </Col>
            </Form.Group>
            <Form.Group as={Row} className={`${styles.default} mb-4`}>
              <Form.Label
                column
                sm={2}
                className={`${styles.groupInfo}`}
                htmlFor="introduceDetail"
              >
                그룹 정보
                <span className={`${styles.badge}`}>필수</span>
              </Form.Label>
              {/* <div className= {`${styles.groupDetail}  ${styles.groupForm} `} style={{ border: '2px solid gray' }}> */}
              <Col
                className={`${styles.groupDetail}  ${styles.groupForm} mb-4`}
                style={{
                  border: "2px solid #dddddd",
                  padding: "25px 20px 31px 24px",
                }}
              >
                <Row
                  className="mb-2"
                  style={{ display: "flex", gap: "0.5rem" }}
                >
                  <Col className="col-8 col-md-5">
                    <InputGroup>
                      <Form.Label
                        className={` ${styles.defaultLayout} ${styles.groupDetailLabel}`}
                        htmlFor="memberCount"
                      >
                        모집정원
                      </Form.Label>
                      <Form.Control
                        id="memberCount"
                        name="memberCount"
                        type="number"
                        value={memberCount}
                        onChange={handleChange}
                        placeholder="2~100명 사이"
                        required
                      />
                      <InputGroup.Text>명</InputGroup.Text>
                      <Form.Control.Feedback type="invalid">
                        모집 희망인원을 입력해주세요!
                      </Form.Control.Feedback>
                    </InputGroup>
                  </Col>
                  {/* <Col className='col-8 col-md-4 mb-4'>
                                        <InputGroup>
                                            <Form.Label className={`${styles.defaultLayout} ${styles.groupDetailLabel}`} htmlFor="cost" >참가비용</Form.Label>
                                            <Form.Control 
                                                    id='cost' 
                                                    name='cost' 
                                                    type="number" 
                                                    value={cost}
                                                    onChange={handleChange}
                                                    placeholder="0~1,000,000원 사이" />
                                            <InputGroup.Text>원</InputGroup.Text>
                                            <Form.Control.Feedback type="invalid">활동비용을 입력해주세요!</Form.Control.Feedback>
                                        </InputGroup>
                                    </Col> */}
                  <Col className="col-8 col-md-5">
                    <InputGroup>
                      <Form.Label
                        className={`${styles.defaultLayout} ${styles.groupDetailLabel}`}
                        htmlFor="location"
                      >
                        활동지역
                      </Form.Label>
                      <Form.Select
                        name="location"
                        onChange={handleLocationChange}
                        aria-label="Default select example"
                        required
                      >
                        <option>활동지역 선택</option>
                        <option value="1">전국</option>
                        <option value="2">서울</option>
                        <option value="3">인천</option>
                        <option value="4">경기</option>
                        <option value="5">대전</option>
                        <option value="6">대구</option>
                        <option value="7">부산</option>
                        <option value="8">광주</option>
                        <option value="9">울산</option>
                        <option value="10">세종</option>
                        <option value="11">강원</option>
                        <option value="12">충북</option>
                        <option value="13">충남</option>
                        <option value="14">전북</option>
                        <option value="15">전남</option>
                        <option value="16">경북</option>
                        <option value="17">경남</option>
                        <option value="18">제주</option>
                      </Form.Select>
                      <Form.Control.Feedback type="invalid">
                        주활동 지역을 선택해주세요!
                      </Form.Control.Feedback>
                    </InputGroup>
                  </Col>
                </Row>
                <Row
                  className="col-12 col-md-6"
                  style={{ marginTop: "-1.5rem" }}
                >
                  <Col
                    style={{
                      display: "inline-block",
                      marginRight: "1rem",
                      marginTop: "2rem",
                    }}
                  >
                    <Form.Label
                      className={`${styles.defaultLayout} ${styles.groupDetailLabel}`}
                      htmlFor="target"
                      style={{ top: "6.6rem", left: "2rem" }}
                    >
                      가입 대상자
                    </Form.Label>
                    <Form.Control
                      id="target"
                      name="target"
                      type="text"
                      value={target}
                      onChange={handleChange}
                      placeholder="가입 대상자를 입력하세요"
                      required
                    />
                    <Form.Control.Feedback type="invalid">
                      가입 대상자를 입력해주세요!
                    </Form.Control.Feedback>
                  </Col>
                </Row>
                <Row className="col-12 mb-4">
                  <Form.Label className="mt-4" htmlFor="target">
                    주 모임장소 설정
                  </Form.Label>
                  <Col className={`${styles.address} col-md-2`}>
                    <Form.Control
                      id="zipcode"
                      name="zipcode"
                      type="text"
                      value={zipcode}
                      onChange={handleChange}
                      placeholder="우편번호"
                      required
                    />
                  </Col>
                  <Col
                    className={`${styles.address} col-md-6`}
                    style={{ padding: 0 }}
                  >
                    <Form.Control
                      id="address"
                      name="address"
                      type="text"
                      value={address}
                      onChange={handleChange}
                      placeholder="장소명 또는 주소를 입력해주세요."
                      required
                    />
                    <Form.Control.Feedback type="invalid">
                      주 모임장소를 입력해주세요!
                    </Form.Control.Feedback>
                  </Col>
                  <Col md="auto">
                    <Button
                      variant="secondary"
                      size="md"
                      onClick={searchAddress}
                    >
                      검색
                    </Button>
                  </Col>
                  <Col className={`${styles.address} col-md-8 mt-2`}>
                    <Form.Control
                      id="address2"
                      name="address2"
                      type="text"
                      value={address2}
                      onChange={handleChange}
                      placeholder="나머지 주소를 입력해 주세요."
                      required
                    />
                    <Form.Control.Feedback type="invalid">
                      나머지 장소를 입력해주세요!
                    </Form.Control.Feedback>
                  </Col>
                  <Modal show={modalState} onHide={() => setModalState(false)}>
                    <Modal.Header closeButton>
                      <Modal.Title>주소 검색</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                      <SomoimPostcode
                        onAddressChange={handleAddressChange}
                        isOpen={modalState}
                        onClose={() => setModalState(false)}
                      />
                    </Modal.Body>
                  </Modal>
                </Row>
              </Col>
              {/* </div> */}
            </Form.Group>

            <Form.Group as={Row} className={`${styles.default} mb-4`}>
              <Form.Label
                column
                sm={2}
                className={`${styles.groupInfo}`}
                htmlFor="introduceDetail"
              >
                개설자 정보
                <span className={`${styles.badge}`}>필수</span>
              </Form.Label>
              <Col
                className={`${styles.groupDetail}  ${styles.groupForm} mb-4`}
                style={{
                  border: "2px solid #dddddd",
                  padding: "21px 20px 31px 24px",
                }}
              >
                <Row className="col-12 mb-3 d-flex align-items-center">
                  <Form.Label
                    className="col-1 mt-3"
                    htmlFor="createdId"
                    style={{ width: "max-content" }}
                  >
                    이&nbsp;&nbsp;&nbsp;&nbsp;름 :{" "}
                  </Form.Label>
                  <Col className={`col-md-6`}>
                    <Form.Control
                      id="createdId"
                      name="createdId"
                      type="text"
                      value={name}
                      // placeholder="John Doe"
                      readOnly
                    />
                  </Col>
                </Row>
                <Row className="col-12 mb-3 d-flex align-items-center">
                  <Form.Label
                    className="col-1 mt-3"
                    htmlFor="accountEmail"
                    style={{ width: "max-content" }}
                  >
                    이메일 :{" "}
                  </Form.Label>
                  <Col className={`col-md-6`}>
                    <Form.Control
                      id="accountEmail"
                      name="accountEmail"
                      type="email"
                      value={accountEmail}
                      onChange={handleChange}
                      placeholder="이메일 형식(예: xxx@xxx.xxx)에 맞게 기입해주세요"
                      required
                    />
                    <Form.Control.Feedback type="invalid">
                      이메일 주소를 입력해주세요!
                    </Form.Control.Feedback>
                  </Col>
                </Row>
                <Row className="col-12 mb-3 d-flex align-items-center">
                  <Form.Label
                    className="col-1 mt-3"
                    htmlFor="accountPhone"
                    style={{ width: "max-content" }}
                  >
                    연락처 :{" "}
                  </Form.Label>
                  <Col className={`col-md-6`}>
                    <Form.Control
                      id="accountPhone"
                      name="accountPhone"
                      type="number"
                      value={accountPhone}
                      onChange={handleChange}
                      placeholder="숫자만 입력해주세요('-' 입력x)"
                      required
                    />
                    <Form.Control.Feedback type="invalid">
                      연락처를 입력해주세요!
                    </Form.Control.Feedback>
                  </Col>
                </Row>
              </Col>
              {/* </div> */}
            </Form.Group>
            <br />

            <div className="d-grid gap-1">
              <Button
                variant="secondary"
                type="submit"
                disabled={validated}
                style={{ backgroundColor: validated ? "black" : "" }}
              >
                소모임 개설하기
              </Button>
            </div>
          </Form>
        </div>
      </Container>

      <div style={{ height: 50 }}></div>
      <Footer></Footer>
    </div>
  );
};

export default SomoimNew;
