import React, { useState } from "react";
import CategoryBtn from "../CategoryBtn";

const CommunityBtnContainer = ({ selectedCategory, setSelectedCategory }) => {
  const handleCategoryClick = (category) => {
    setSelectedCategory(category);
  };

  return (
    <>
      <CategoryBtn
        isSelect={
          selectedCategory === "실시간 인기글" ? "selected" : "notSelected"
        }
        text="실시간 인기글"
        onClick={() => handleCategoryClick("실시간 인기글")}
        size="col-4"
        height="45px"
        fontSize="1.3rem"
      />

      <CategoryBtn
        isSelect={
          selectedCategory === "자랑 게시판" ? "selected" : "notSelected"
        }
        text="자랑 게시판"
        onClick={() => handleCategoryClick("자랑 게시판")}
        size="col-4"
        height="45px"
        fontSize="1.3rem"
      />
    </>
  );
};

export default CommunityBtnContainer;
